mapper.update:
UPDATE images
SET img_name = #{imgName}, ori_img_name = #{oriImgName}, img_url = #{imgUrl},
    file_size = #{fileSize}, upload_date = #{uploadDate}, image_type = #{imageType}
WHERE id = #{id}

mapper.select:
SELECT img_name, ori_img_name, img_url, file_size, upload_date, image_type, type_id
FROM images
WHERE type_id = #{typeId} AND image_type = #{type}
